const screenShot = () => {
  const image = document.querySelector('#image');
  const canvas = document.querySelector('#canvas');
  const test = document.querySelector('#test');
  const data = image.toDataURL('image/png');
  console.log(data);
  test.src = data;
}

export default screenShot;
